webpackJsonp([0xbf810a529942],{298:function(e,n){e.exports={data:{site:{siteMetadata:{title:"Annalisa Valente"}},contentfulPageSelectorPanel:{pages:[{name:"Macmillan Cancer Support",title:null,slug:"/projects/brave-the-shave/",description:"Redesigning Brave the Shave microsite"},{name:"Google",title:"Google",slug:"/projects/google/",description:"Interaction designer contractor in the Google Play Console team"},{name:"The Telegraph",title:"The Telegraph",slug:"/projects/the-telegraph/",description:"Working on various digital newspaper touch points"},{name:"Nectar ",title:"Nectar",slug:"/projects/nectar/",description:"  New way of discovering Nectar brands for web and iOS-Android "},{name:"The Telegraph puzzle ",title:null,slug:"/projects/the-telegraph-puzzle/",description:"Redesign the experience of the Telegraph puzzle website"},{name:"RememberÂ names",title:null,slug:"/projects/face-name/",description:"Concept of an Android experience that helps to match faces to names"},{name:"MyMail & Nectar",title:"MyMail & Nectar",slug:"/projects/mymail-and-nectar/",description:"Launch of MyMail and Nectar proposition"},{name:"Local Cancer Intelligence",title:"Local cancers intelligence",slug:"/projects/local-cancer-intelligence/",description:"New filter system for the LCI website"},{name:"Mindfulness Moments",title:"Mindfulness Moments",slug:"/projects/mindfulness-moments/",description:"Building a new fundraising proposition"},{name:"Calvin Klein",title:"Calvin Klein",slug:"/projects/calvin-klein/",description:"Designing microsite for Calvin Klein Italy"}]},contentfulPage:{slug:"/projects/mighty-hikes",password:null,panels:[{__typename:"ContentfulContentPanel",content:{content:"__My role:__ UX Lead\n\n__Project scope:__ \n- A new proposition to allow event participants to have all info, touchpoints needed in a single website\n- A user experience review and analysis of the Mighty hikes journey from awareness through to registration completion \n\n __Project objectives:__\n- Improve taxonomy and content of the site based on identifying user needs\n- Improve navigation and flow\n- Improve content on landing pages and single event pages\n- Improve logic of FAQs\n- Create a starting point where to iterate following by appropriate user research:\n1. Make registration of individual and team in one single platform\n2. Gamification and challenge (e.g. Go sober)\n3. Sign up/Donation journey in a single touchpoint\n\n__Approach__",childMarkdownRemark:{html:"<p><strong>My role:</strong> UX Lead</p>\n<p><strong>Project scope:</strong> </p>\n<ul>\n<li>A new proposition to allow event participants to have all info, touchpoints needed in a single website</li>\n<li>A user experience review and analysis of the Mighty hikes journey from awareness through to registration completion </li>\n</ul>\n<p> <strong>Project objectives:</strong></p>\n<ul>\n<li>Improve taxonomy and content of the site based on identifying user needs</li>\n<li>Improve navigation and flow</li>\n<li>Improve content on landing pages and single event pages</li>\n<li>Improve logic of FAQs</li>\n<li>Create a starting point where to iterate following by appropriate user research:</li>\n<li>Make registration of individual and team in one single platform</li>\n<li>Gamification and challenge (e.g. Go sober)</li>\n<li>Sign up/Donation journey in a single touchpoint</li>\n</ul>\n<p><strong>Approach</strong></p>"}}},{__typename:"ContentfulHeaderPanel",text:null},{__typename:"ContentfulContentPanel",content:{content:"__Data, research and findings__\n\nData on Mighty Hikes:",childMarkdownRemark:{html:"<p><strong>Data, research and findings</strong></p>\n<p>Data on Mighty Hikes:</p>"}}},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulContentPanel",content:{content:"__ Competitor research__",childMarkdownRemark:{html:"<p>__ Competitor research__</p>"}}},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulContentPanel",content:{content:"__ Benchmarks analysis:__\n- Divide the content in two different sections: before and after the event\n- Give the right amount of info to register in a simple way\n- Search bar in the homepage to allow to search for an event\n\n__Research and findings__\n\n__Exit quantitative/qualitative surveys:__\nThe survey was sent after the events and it collected around __4.000__ replies from participants.",childMarkdownRemark:{html:"<p>__ Benchmarks analysis:__</p>\n<ul>\n<li>Divide the content in two different sections: before and after the event</li>\n<li>Give the right amount of info to register in a simple way</li>\n<li>Search bar in the homepage to allow to search for an event</li>\n</ul>\n<p><strong>Research and findings</strong></p>\n<p><strong>Exit quantitative/qualitative surveys:</strong>\nThe survey was sent after the events and it collected around <strong>4.000</strong> replies from participants.</p>"}}},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulContentPanel",content:{content:"__Facebook qualitative survey:__\nThe survey was posted in the facebook page of the event and it collected __163 replies__. It covers areas that were not covered with the exit survey to understand user needs and behaviour for subjects like training and after the hike activities.",childMarkdownRemark:{html:"<p><strong>Facebook qualitative survey:</strong>\nThe survey was posted in the facebook page of the event and it collected <strong>163 replies</strong>. It covers areas that were not covered with the exit survey to understand user needs and behaviour for subjects like training and after the hike activities.</p>"}}},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulContentPanel",content:{content:"__Findings from qualitative research__",childMarkdownRemark:{html:"<p><strong>Findings from qualitative research</strong></p>"}}},{__typename:"ContentfulImagePanel",fullWidth:null,media:null},{__typename:"ContentfulImagePanel",fullWidth:null,media:{description:"",file:{url:"//images.ctfassets.net/wk0hltq7eofw/18nSOCk3Myqn3jwzpSDwwf/50e8daaf09914b30d3152f8a09641424/Screen_Shot_2019-02-04_at_23.13.49.png"}}}]}},pathContext:{slug:"/projects/mighty-hikes",p:"35675e68f4b5af7b995d9205ad0fc43842f16450"}}}});
